import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router';
// import './TeamDetail.css'
import  Button from 'react-bootstrap';

const TeamDetail = () => {
    let { idTeam } = useParams();
    const [detail,setDetail]=useState([]);

    const {intFormedYear,strCountry,strSport,strGender}=detail;
    useEffect(()=>{
        const url=`https://www.thesportsdb.com/api/v1/json/1/lookupteam.php?id=${idTeam}`;
        fetch(url)
        .then(res=>res.json())
        .then(data=>setDetail(data.teams[0]))
    },[])
    return (
        <div className="Team-Detail">
            <div>
            {/* <h1>This is TeamDetail:{idTeam}</h1> */}



            <div className="text col-md-6 my-6">
            <h1>Primer League</h1>
            <p>Founded:{intFormedYear}</p>
            <p>Country:{strCountry}</p>
            <p>Sports Type:{strSport}</p>
            <p>Gender:{strGender}</p>
            
            </div>
           </div>
           <div className="col-md-6 my-6">
        <img style={{width:'100px'}} src="../../image.jpg" alt=""/>
        </div>
        </div>
        
        
    );
};

export default TeamDetail;
