import React from 'react';
import { Card, Button } from 'react-bootstrap';
import './Team.css'

const Team = (props) => {
    const { strAlternate, strCountry, strTeamBadge } = props.teams;
    const styleTeam = {
        border: '2px solid red',
        margin: '10px',
        borderRadius: '20px',
        display:'float-left'
        

    }
    return (

        <div style={styleTeam} className="col-md-3 my-3 team">

            <div className="row-md-4 my-4">
            <Card style={{ width: '18rem' }}>

<Card.Body>
<img style={{width:'150px'}} src={strTeamBadge} alt=""/>
    <Card.Title>Name:{strAlternate}</Card.Title>
    <Card.Subtitle className="mb-2 text-muted">Country:{strCountry}</Card.Subtitle>
    <Button variant="primary">Primary</Button>{' '}
    
</Card.Body>
</Card>

            </div>
        </div>
    );
};

export default Team;
