import React from 'react';
import { Card, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import './Team.css'

const Team = (props) => {
    const { strAlternate, strCountry, strTeamBadge,idTeam } = props.teams;
    const styleTeam = {
        border: '2px solid red',
        margin: '10px',
        borderRadius: '20px',
        display:'float-left'
        

    }
    return (

        <div style={styleTeam} className="col-md-3 my-3 team">

            <div className="row-md-3 my-3">
            <Card style={{ width: '18rem' }}>

<Card.Body>
<img style={{width:'150px'}} src={strTeamBadge} alt=""/>
    <Card.Title>Name:{strAlternate}</Card.Title>
    <Card.Subtitle className="mb-2 text-muted">Country:{strCountry}</Card.Subtitle>
    <Link to={`/Team/${idTeam}`}><Button variant="primary">Details{idTeam}</Button></Link>
    
    
</Card.Body>
</Card>

            </div>
        </div>
    );
};

export default Team;
