import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router';
import './TeamDetail.css'
import image from '../../image/female.png'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCoffee } from '@fortawesome/free-solid-svg-icons'


// import Button from 'react-bootstrap';

const TeamDetail = () => {
    let { idTeam } = useParams();
    const [detail, setDetail] = useState([]);

    const { intFormedYear, strCountry, strSport, strGender } = detail;
    useEffect(() => {
        const url = `https://www.thesportsdb.com/api/v1/json/1/lookupteam.php?id=${idTeam}`;
        fetch(url)
            .then(res => res.json())
            .then(data => setDetail(data.teams[0]))
    }, [])
    return (

<div class="container ">
  <div class="row Team-Detail ">
    <div class="col-md-6  my-6">
    <h1>Primer League</h1>
                <p><FontAwesomeIcon icon={<i class="fas fa-chevron-circle-right"></i>} /> Founded:{intFormedYear}</p>
                <p>Country:{strCountry}</p>
                <p>Sports Type:{strSport}</p>
                <p>Gender:{strGender}</p>
    </div>
    <div class="col-md-6 my-6">
        <img style={{width:'300px'}} src={image} alt=""/>
        
    </div>
  </div>
  
    
    
  </div>


    );
};

export default TeamDetail;
